import apiService from 'api/apiService'

export default class EntityApi {
    constructor(uri) {
        this.uri = uri
    }

    get = (params = {}) => {
        return apiService.get(this.uri, params);
    }

    getOne = (id, params = {}) => {
        return apiService.get(`${this.uri}${id}/`, params);
    }

    post = (data, params = {}) => {
        return apiService.post(this.uri, data, params);
    }

    put = (id, data, params = {}) => {
        return apiService.put(`${this.uri}${id}/`, data, params);
    }

    patch = (id, data, params = {}) => {
        return apiService.patch(`${this.uri}${id}/`, data, params);
    }

    delete = (id, params = {}) => {
        return apiService.delete(`${this.uri}${id}/`, params);
    }
} 